mapper.insert:
INSERT INTO cart (cart_id, user_id, shoes_id, quantity, size)
SELECT COALESCE(MAX(cart_id) + 1, 1), #{user_id}, #{shoes_id}, #{quantity}, #{size}
FROM cart

mapper.select:
select shoes_id
from cart	 
where shoes_id = #{shoes_id}

mapper.update:
update cart
set is_delete = 1
where shoes_id = #{shoes_id}
