mapper.select:
SELECT shoes_id, shoes_name, final_price
FROM shoes
LIMIT #{startRow},#{pageSize};

SELECT count(*) 
FROM shoes 
where is_delete = 0;

select user_id, name, email
from user
where user_id = "admin";

mapper.update:
update shoes
set shoes_price = #{shoes_price}, sales = #{sales}, final_price = #{final_price}
where shoes_name = #{shoes_name};

mapper.select:
select user_id, name, birthday, adress, detail_adress, email, phone
from User 
where user_id = #{user_id};

SELECT user_id, name, adress, birthday, phone
FROM user
WHERE user_id != "admin";

SELECT shoes_id
FROM cart
WHERE shoes_id = #{shoes_id}

mapper.update:
update cart
set is_delete = 1
where shoes_id = #{shoes_id}

mapper.select:
select title, content, write_date
from notice_board
where idx = #{idx}

mapper.update:
SET @row_number = 0;

UPDATE notice_board
SET idx = (@row_number := @row_number + 1)
ORDER BY idx;
