mapper.select:
SELECT shoes_id, shoes_name, shoes_price
FROM shoes
WHERE shoes_name = CONCAT('%',#{shoes_Name},'%');

select user_id, name, birthday, adress, detail_adress, email, phone
from User 
where user_id = #{user_id};

SELECT shoes_id
FROM cart
WHERE shoes_id = #{shoes_id}

mapper.update:
update cart
set is_delete = 1
where shoes_id = #{shoes_id}

mapper.select:
select title, content, write_date
from notice_board
where idx = #{idx}
