mapper.insert:
INSERT INTO User (idx, user_id, birthday, pw, adress, detail_adress, email, salt, phone, is_Status)
SELECT COALESCE(MAX(idx) + 1, 1), #{id}, #{birthday}, #{pw}, #{adress}, #{detail_adress}, #{email}, #{salt}, #{phone}, #{is_Status}
FROM User

mapper.select:
select user_id
from User 
where user_id = #{id}

select salt
from User 
where user_id = #{id}

select pw
from User 
where user_id = #{id}

UPDATE User
SET pw = #{pw}, salt = #{salt}
WHERE idx IN (SELECT temp.idx FROM (SELECT idx FROM User WHERE id = #{id}) AS temp);
