mapper.insert:
INSERT INTO User (idx, user_id, name, birthday,  pw, adress, detail_adress, email, salt, phone, is_Status)
SELECT COALESCE(MAX(idx) + 1, 1), #{id}, #{name}, #{birthday}, #{pw}, #{adress}, #{detail_adress}, #{email}, #{salt}, #{phone}, #{is_Status}
FROM User

mapper.select:
select user_id
from user	 
where user_id = #{id}

select salt
from User 
where user_id = #{id}

select pw
from User 
where user_id = #{id}

UPDATE User
SET pw = #{pw}, salt = #{salt}
WHERE user_id IN (SELECT temp.user_id FROM (SELECT user_id FROM User WHERE user_id = #{id}) AS temp);
